      loan_money, loan_date, 
repayment_date
      )
    values (#{loanId,jdbcType=INTEGER}, #{contractId,jdbcType=VARCHAR}, #{loanName,jdbcType=VARCHAR}, 
      #{loanMoney,jdbcType=DOUBLE}, #{loanDate,jdbcType=DATE}, #{
repaymentDate,jdbcType=VARCHAR}
      )

